INSERT INTO `training` (`id`, `username`, `pariwisata_id`, `komentar`, `kategori`) VALUES
(1, 'Bertha Rayuni', 1, '1 yg tidak merah airnya namun memiliki view yang indah. Recomended banget buat para teman2 yg cari destinasi wisata.', 1),
(2, 'fuad pratama', 1, 'Perjalanan kali ini ke 1 bersama keluarga yang gokil, Ah mantap! Semoga fasilitas disini semakin bagus', 1),
(3, 'ANDIMURSIDI', 1, 'Saat saya dan keluarga berkunjung siang hari cuaca cerah, jakan menuju lokasi bagus, rambu penunjuk juga jelas. Indahnya 1 tidak terlihat karena tertutup pepohonan. Waktu terbaik untuk berkunjung saat Sunset. Waktu tempuh dari pusat kota Banyuwangi sekitar 2 jam', 1),
(4, 'Lovina', 1, 'Pantai masih perawan dan lumayanlah...sayang lokasinya kurang terawat, toilet2 jg asal..mudah2 an kedepan pemerintah daerah lebih memperhatikan ...spy wisatanya di banyuwangi tambah Ok', 1),
(5, 'ariena', 1, 'datang ke pantai Merah di saat menjelang senja, menikmati detik-detik datangnya sunset dan suasana pantai dengan deburan ombak seirama detak jantung yang mengalun. Alam indonesia merupakan Ciptaan yang Maha Kuasa tak ada yang dapat Menandingi CiptaanNYA.', 1),
(6, '_deddygekijo1', 1, 'Salah satu wisata recomend di Banyuwangi. Kalau ke pantai ini sebaiknya sore hari menjelang senja, agar dapat sunsetnya. Diperhatikan juga agar tidak duduk sembarangan di tempat duduk yang disediakan, karena setelah kita berdiri akan dicharge oleh pemilik kursi.', 1),
(7, 'Eryc Octaviano', 1, 'Sebenarnya pemandangan saat sore sangat indah, pasirnya pun putih bersih. Tapi pada musim liburan sangat crowded sehingga tidak bisa menikmati keindahannya, semakin tidak sepadan dibanding perjalanan yg cukup jauh ke pualu merah (2-3 jam dari banyuwangi kota). Saran saya lebih baik ke 1 saat low season.', 2),
(8, 'honestkaro', 1, 'Pantainya bersih, indah cuma sayang akses jalan menuju lokasi tidak begitu mulus dan cenderung sempit. Santai sambil menikmati kelapa muda seharga 10.000/buah dengan menyewa payung tempat santai dengan 2 kursi utk selonjoran seharga 20.000/jam. Tapi sayang, jam 6 sore udah pada tutup. Kita yang hendak santai, selonjoran diminta untuk bergegas. Sunsetnya indah.. Jika dikelola secara maksimal, pasti bakalan rame banget...', 1),
(9, 'Made gimbal', 1, '1 memang tdk semerah namanya,tapi sangat nyaman untuk refresing,pantainya bersih dan gelombangnya eksitis', 2),
(10, 'julihdiana', 1, 'Ketika saya kesana air laut agak sedikit surut sehingga memungkinkan saya jalan menyusuri pantai menuju 1, namun harus berhati-hati karena ada banyak bulu babi.', 1),
